// Slide show functionality

let slideIndex = 1;
showSlides();

// Function to navigate slides
function plusSlides(n) {
  slideIndex += n;
  showSlides();
}

// Function to show the current slide
function currentSlide(n) {
  slideIndex = n;
  showSlides();
}

// Show slides (manual and automatic)
function showSlides() {
  const slides = document.getElementsByClassName("mySlides");
  const dots = document.getElementsByClassName("dot");

  if (!slides || slides.length === 0) {
    console.error("No slides found.");
    return;
  }

  if (!dots || dots.length === 0) {
    console.error("No dots found.");
    return;
  }

  if (slideIndex > slides.length) {
    slideIndex = 1;
  }
  if (slideIndex < 1) {
    slideIndex = slides.length;
  }

  for (let i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  for (let i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }

  slides[slideIndex - 1].style.display = "block";
  dots[slideIndex - 1].className += " active";

  slideIndex++;
  setTimeout(showSlides, 3000);
}

 
function displayProducts() {
  const containers = document.querySelectorAll(".product-container");

  containers.forEach((container) => {
    const category = container.getAttribute("data-category");
    const filteredProducts = products.filter(
      (product) => product.category === category || category === "all"
    );

    filteredProducts.forEach((product) => {
      const productElement = document.createElement("div");
      productElement.classList.add("product");
      productElement.innerHTML = `
        <a href="product.html?slug=${product.slug}">
          <img src="${product.image}" alt="${product.name}">
          <h3>${product.name}</h3>
          <p>Price: Ksh ${product.price}</p>
          
          ${
            product.inStock
              ? ""
              : '<p class="out-of-stock">Out of Stock</p>'
          }
        </a>
      `;
      container.appendChild(productElement);
    });
  });
}

// Run on homepage load
document.addEventListener("DOMContentLoaded", function () {
  if (document.body.id === "home") {
    displayProducts();
  }
});



function normalizeSlug(slug) {
  return slug
    .toLowerCase()// 
    .replace(/[^a-z0-9+-]+/g, '-') 
    .replace(/-+/g, '-')          
    .replace(/^-|-$/g, '');       
}




// Function to display product details
function displayProductDetails() {
  const urlParams = new URLSearchParams(window.location.search);
  const slug = urlParams.get("slug");

  console.log("URL Slug Retrieved:", slug);

  if (!slug) {
    console.error("No slug found in URL.");
    document.querySelector(".product-details").innerHTML = "<p>Product not found!</p>";
    return;
  }

  const normalizedSlug = normalizeSlug(slug);
  console.log("Normalized slug from URL:", normalizedSlug);

  if (!Array.isArray(products) || products.length === 0) {
    console.error("Products array is empty or not loaded.");
    document.querySelector(".product-details").innerHTML =
      "<p>Product data is missing!</p>";
    return;
  }

  try {
    const product = products.find((p) => p && normalizeSlug(p.slug) === normalizedSlug);

    if (product) {
      console.log("Product found:", product);

      const productDetailsContainer = document.querySelector(".product-details");
      if (!productDetailsContainer) {
        console.error("Product details container not found!");
        return;
      }

      let quantityOptions = product.inStock
        ? Array.from({ length: 10 }, (_, i) => `<option value="${i + 1}">${i + 1}</option>`).join("")
        : '<option value="0">Out of Stock</option>';

      productDetailsContainer.innerHTML = `
        <div class="product-info">
          <div class="product-image">
            <img src="${product.image}" alt="${product.name}">
          </div>
          
            <h1>${product.name}</h1>
            <p>Price: Ksh ${product.price}</p>
            
            <label for="quantity">Quantity:</label>
            <select id="quantity" ${product.inStock ? "" : "disabled"}>
              ${quantityOptions}
            </select>
            ${product.inStock ? "" : '<p class="out-of-stock">Out of Stock</p>'}
            <button id="add-to-cart" ${product.inStock ? "" : "disabled"}>Add to Cart</button>
          </div>
        </div>
        <div class="long-description">${product.longDescription || ""}</div>
      `;

      displayRelatedProducts(product);

      const addToCartButton = document.getElementById("add-to-cart");
      if (addToCartButton) {
        addToCartButton.addEventListener("click", () => {
          const quantity = parseInt(document.getElementById("quantity").value);
          if (quantity > 0) {
            product.quantity = quantity;
            addToCart(product);
            alert("Product added to cart!");
          } else {
            alert("Please select a valid quantity!");
          }
        });
      }
    } else {
      console.error("Product not found for slug:", normalizedSlug);
      console.log("Available Products:", products);
      document.querySelector(".product-details").innerHTML =
        "<p>Product not found!</p>";
    }
  } catch (error) {
    console.error("Error while displaying product details:", error);
    document.querySelector(".product-details").innerHTML =
      "<p>An error occurred while loading the product details.</p>";
  }
}

// Function to display related products
function displayRelatedProducts(currentProduct) {
  const relatedProductsContainer = document.querySelector(".related-products");

  if (!relatedProductsContainer) {
    console.warn("Related products container not found in the DOM.");
    return;
  }

  const relatedProducts = products.filter(
    (product) =>
      product.category === currentProduct.category && product.slug !== currentProduct.slug
  );

  if (relatedProducts.length > 0) {
    relatedProductsContainer.innerHTML = `
      <h2>Related Products</h2>
      <div class="related-products-grid">
        ${relatedProducts
          .map(
            (product) => `
          <div class="related-product-card">
            <a href="product.html?slug=${product.slug}">
              <img src="${product.image}" alt="${product.name}">
              <h3>${product.name}</h3>
              <p>Price: Ksh ${product.price}</p>
            </a>
          </div>
        `
          )
          .join("")}
      </div>
    `;
  } else {
    relatedProductsContainer.innerHTML = "<p>No related products found.</p>";
  }
}

// Initialize displayProductDetails on DOMContentLoaded
document.addEventListener("DOMContentLoaded", function () {
  if (document.body.id === "product") {
    displayProductDetails();
  }
});

// Function to add product to cart
function addToCart(product) {
  let cart = JSON.parse(localStorage.getItem("cart")) || [];
  const existingProductIndex = cart.findIndex((item) => item.slug === product.slug);

  if (existingProductIndex !== -1) {
    cart[existingProductIndex].quantity += 1;
  } else {
    product.quantity = 1;
    cart.push(product);
  }

  localStorage.setItem("cart", JSON.stringify(cart));
}

// Event listener for Add to Cart button
document.addEventListener("DOMContentLoaded", function () {
  const addToCartButton = document.getElementById("add-to-cart");

  if (addToCartButton) {
    addToCartButton.addEventListener("click", function () {
      const urlParams = new URLSearchParams(window.location.search);
      const slug = urlParams.get("slug");
      const product = products.find((p) => p &&p.slug === slug);
      const quantity = parseInt(document.getElementById("quantity").value);

      if (product && quantity > 0) {
        product.quantity = quantity;
        addToCart(product);
        alert("Product added to cart!");
        window.location.href = "cartpage.html";
      } else {
        alert("Error adding product to cart or product is out of stock!");
      }
    });
  } else {
    console.error("Add to Cart button not found!");
  }
});